	self.version = 6.5
	
	self.lolPatch = GetGameVersion and GetGameVersion():sub(1,3) == "6.5" and 1 or 2
	self.notPresentPatch = GetGameVersion and GetGameVersion():sub(1,3) ~= "6.4" and GetGameVersion():sub(1,3) ~= "6.5"
	self.cn = self.notPresentPatch
	
	--Send--
		self.moveHeader = self.lolPatch == 1 and 217 or 170
		self.spellHeader = self.lolPatch == 1 and 270 or 73
		
		--cspell2
		self.cspell2Header = self.lolPatch == 1 and 109 or 268
		self.cspell2Pos = self.lolPatch == 1 and 22 or 18
